package com.asdeporte.asdeportev2.ui.home.views.tabsimport android.content.Contextimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.widget.FrameLayoutimport androidx.recyclerview.widget.LinearLayoutManagerimport com.asdeporte.asdeportev2.databinding.EventDetailViewBenefitPlusBindingimport com.asdeporte.asdeportev2.ui.home.adapters.tabs.kitDelivery.BenefitPlusAdapterimport com.asdeporte.asdeportev2.ui.home.models.tabs.plusMembership.PlusMembershipModelclass EventDetailBenefitPlusView @JvmOverloads constructor(    context: Context, attrs: AttributeSet? = null, defStyle: Int = 0) : FrameLayout(context, attrs, defStyle) {    private var binding: EventDetailViewBenefitPlusBinding    init {        binding = EventDetailViewBenefitPlusBinding.inflate(LayoutInflater.from(context), this, true)    }    fun setData(plusMemberships: List<PlusMembershipModel>) {        val adapter = BenefitPlusAdapter(plusMemberships)        binding.rvBenefitsPlus.adapter = adapter        binding.rvBenefitsPlus.setHasFixedSize(true)        binding.rvBenefitsPlus.layoutManager = LinearLayoutManager(context, LinearLayoutManager.HORIZONTAL, false)        binding.rvBenefitsPlus.isNestedScrollingEnabled = true    }}