package com.asdeporte.asdeportev2.ui.home.views.tabsimport android.content.Contextimport android.text.Htmlimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.widget.FrameLayoutimport com.asdeporte.asdeportev2.databinding.EventDetailViewRecommendationCardBindingclass EventDetailRecommendationCardView @JvmOverloads constructor(        context: Context,        attrs: AttributeSet? = null,        defStyle: Int = 0) : FrameLayout(context, attrs, defStyle) {    private var binding: EventDetailViewRecommendationCardBinding    init {        binding = EventDetailViewRecommendationCardBinding.inflate(LayoutInflater.from(context), this, true)        var tvTrainingDescription: String = binding.tvTrainingDescription.text.toString()        tvTrainingDescription = tvTrainingDescription.replace(            "5 puntos que todo corredor debe saber.",            "<font color=#FF6A00>5 puntos que todo corredor debe saber.</font>"        )        binding.tvTrainingDescription.text = Html.fromHtml(tvTrainingDescription)        var tvMarketplaceDescription: String = binding.tvMarketplaceDescription.text.toString()        tvMarketplaceDescription = tvMarketplaceDescription.replace(            "Tenis de Trail, cangurera, calcetines.",            "<font color=#FF6A00>Tenis de Trail, cangurera, calcetines.</font>"        )        binding.tvMarketplaceDescription.text = Html.fromHtml(tvMarketplaceDescription)    }}