package com.asdeporte.asdeportev2.ui.home.views.tabs.plusMembershipimport android.content.Contextimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.widget.FrameLayoutimport androidx.recyclerview.widget.LinearLayoutManagerimport com.asdeporte.asdeportev2.databinding.EventDetailViewPlusMembershipBindingimport com.asdeporte.asdeportev2.ui.home.adapters.tabs.plusMembership.PlusMembershipCardAdapterimport com.asdeporte.asdeportev2.ui.home.models.tabs.plusMembership.PlusMembershipModelclass EventDetailPlusMembershipView @JvmOverloads constructor(        context: Context,        attrs: AttributeSet? = null,        defStyle: Int = 0) : FrameLayout(context, attrs, defStyle) {    private var binding: EventDetailViewPlusMembershipBinding    init {        binding = EventDetailViewPlusMembershipBinding.inflate(LayoutInflater.from(context), this, true)    }    fun setData(plusMemberships: List<PlusMembershipModel>) {        val adapter = PlusMembershipCardAdapter(plusMemberships)        binding.rvData.apply {            this.adapter = adapter            setHasFixedSize(true)            layoutManager = LinearLayoutManager(context, LinearLayoutManager.VERTICAL, false)            isNestedScrollingEnabled = true        }    }}