package com.asdeporte.asdeportev2.ui.home.views.tabs.tourismimport android.content.Contextimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.webkit.WebResourceRequestimport android.webkit.WebSettingsimport android.webkit.WebViewimport android.webkit.WebViewClientimport android.widget.FrameLayoutimport com.asdeporte.asdeportev2.databinding.EventDetailViewTourismBindingimport com.asdeporte.asdeportev2.utils.Constants.URL_LODGING_TOURISMclass EventDetailTourismView @JvmOverloads constructor(        context: Context,        attrs: AttributeSet? = null,        defStyle: Int = 0) : FrameLayout(context, attrs, defStyle) {    private var binding: EventDetailViewTourismBinding    init {        binding = EventDetailViewTourismBinding.inflate(LayoutInflater.from(context), this, true)    }    fun setData() {        binding.webView.webViewClient = object : WebViewClient() {            override fun shouldOverrideUrlLoading(view: WebView?, request: WebResourceRequest?): Boolean {                if (request?.url?.toString()?.startsWith("https") == true) {                    return false                }                return true            }        }        binding.webView.loadUrl(URL_LODGING_TOURISM)        binding.webView.settings.javaScriptEnabled = true        binding.webView.settings.mixedContentMode = WebSettings.MIXED_CONTENT_NEVER_ALLOW    }}