package com.asdeporte.asdeportev2.ui.profile.details.formaterimport com.github.mikephil.charting.components.AxisBaseimport com.github.mikephil.charting.formatter.ValueFormatterimport java.text.DecimalFormatclass AxisAverageForceValueFormatter() : ValueFormatter() {    private val mFormat: DecimalFormat = DecimalFormat("###")    override fun getFormattedValue(value: Float): String {        return mFormat.format(value.toDouble()) + " %"    }}