package com.asdeporte.asdeportev2.ui.profile.details.formaterimport com.github.mikephil.charting.components.AxisBaseimport com.github.mikephil.charting.formatter.ValueFormatterclass AxisAverageForceZonesValueFormatter() : ValueFormatter() {    private val zones = arrayOf(            "", "Zona 1", "Zona 2", "Zona 3", "Zona 4", "Zona 5"    )    override fun getFormattedValue(value: Float): String {        val data = value.toInt()        return if (data < zones.size) zones[data] else ""    }}